from fabric import task

from benchmark.local import LocalBench
from benchmark.logs import ParseError, LogParser
from benchmark.utils import Print
from benchmark.plot import Ploter, PlotError
from benchmark.instance import InstanceManager
from benchmark.remote import Bench, BenchError
from benchmark.utils import PathMaker

n = 16
num_messages = 256
batch_size = 1000
compression_factor = 10

@task
def create(ctx, nodes=n):
    ''' Create a testbed'''
    try:
        InstanceManager.make().create_instances(nodes)
    except BenchError as e:
        Print.error(e)


@task
def destroy(ctx):
    ''' Destroy the testbed '''
    try:
        InstanceManager.make().terminate_instances()
    except BenchError as e:
        Print.error(e)


@task
def start(ctx, max=n):
    ''' Start at most `max` machines per data center '''
    try:
        InstanceManager.make().start_instances(max)
    except BenchError as e:
        Print.error(e)


@task
def stop(ctx):
    ''' Stop all machines '''
    try:
        InstanceManager.make().stop_instances()
    except BenchError as e:
        Print.error(e)


@task
def info(ctx):
    ''' Display connect information about all the available machines '''
    try:
        InstanceManager.make().print_info()
    except BenchError as e:
        Print.error(e)


@task
def install(ctx):
    ''' Install the codebase on all machines '''
    try:
        Bench(ctx).install()
    except BenchError as e:
        Print.error(e)

# We only use the `nodes` parameter in the list of all these parameters. 
@task
def remote(ctx, debug=False):
    ''' Run benchmarks on AWS '''
    bench_params = {
        'faults': 0,
        'nodes': [n],
        'num_messages': num_messages,
        'batch_size': batch_size,
        'compression_factor': compression_factor
    }
    try:
        Bench(ctx).run(bench_params, debug)
    except BenchError as e:
        Print.error(e)

@task
def rerun(ctx, debug=False):
    ''' Run benchmarks on AWS '''
    bench_params = {
        'faults': 0,
        'nodes': [n],
        'num_messages': num_messages,
        'batch_size': batch_size,
        'compression_factor': compression_factor
    }
    try:
        Bench(ctx).justrun(bench_params, debug)
    except BenchError as e:
        Print.error(e)

@task
def plot(ctx):
    ''' Plot performance using the logs generated by "fab remote" '''
    plot_params = {
        'faults': [0],
        'nodes': [10, 20, 50],
        'workers': [1],
        'collocate': True,
        'tx_size': 512,
        'max_latency': [3_500, 4_500]
    }
    try:
        Ploter.plot(plot_params)
    except PlotError as e:
        Print.error(BenchError('Failed to plot performance', e))


@task
def kill(ctx):
    ''' Stop execution on all machines '''
    try:
        Bench(ctx).kill()
    except BenchError as e:
        Print.error(e)


@task
def logs(ctx):
    ''' Print a summary of the logs '''
    ''' Run benchmarks on AWS '''
    bench_params = {
        'faults': 0,
        'nodes': [n],
        'num_messages': num_messages,
        'batch_size': batch_size,
        'compression_factor': compression_factor
    }
    try:
        Bench(ctx).pull_logs(bench_params)
    except ParseError as e:
        Print.error(BenchError('Failed to parse logs', e))
